        translateY: [100, 200],
    });


    return (
        <Grid   container
                direction="column"
                justifyContent="center"
                alignItems="center"
                className="gridSchoolContainer">
            <span  className="background">
                <ParallaxBanner
                    layers={[
                        {
                            image: Background,
                            speed: -15,
                            scale: [1, 1.1],
                            opacity: [0.9, 1],
                        },
                        {
                            image: IconImage,
                            speed: -20,
                            scale:[0.2, 0.2],
